export const SET_SEARCH = '@@controls/SET_CONTROLS';
export const SET_REGION = '@@controls/SET_REGION';

export const setSearch = (search) => ({
    type: SET_SEARCH,
    payload: search
})

export const setRegion = (region) => ({
    type: SET_REGION
})
