import { SET_LOADING, SET_ERROR, SET_COUNTRY, CLEAR_COUNTRY } from "./details-actions"

const initialState = {
    currentCountry: '',
    status: 'idle',
    error: null
}

export const detailsReducer = (state = initialState, {type, payload}) => {
    switch (type) {
        case SET_LOADING:
            return {
                ...state,
                error: null, 
                status: 'loading'
            }
        case SET_ERROR:
            return {
                ...state,
                error: payload,
                status: 'rejected'
            }
        case SET_COUNTRY:
            return {
                ...state,
                status: 'received',
                currentCountry: payload
            }
        case CLEAR_COUNTRY: 
            return initialState
        default:
            return state
    }
}
