import { SET_SEARCH, SET_REGION, CLEAR_CONTROLS } from "./controls-action";

const initialState = {
    search: '',
    region: ''
}

export const controlsReducer = (state = initialState, {type, payload}) => {
    switch (type) {
        case SET_SEARCH:
            return {
                ...state,
                search: payload
            }
        case SET_REGION:
            return{
                ...state,
                region: payload
            }
        case CLEAR_CONTROLS:
            return initialState
        default:
            return state
    }
}
